/**
 * Create popup HTML content
 * @param title
 * @param $content
 * @param $footer
 * @param dialogSize
 * @returns {*|jQuery}
 */
function popupHTML(title, $content, $footer, dialogSize) {
    var $header = $("<div>").append(
        $("<button>").addClass("close").attr({
            "type": "button",
            "data-dismiss": "modal",
            "aria-label": "Close"
        }).append(
            $("<span>").attr({
                "aria-hidden": "true"
            }).html("&times;")
        ),
        $("<h4>").addClass("modal-title").text(title)
    );


    if (typeof $footer == 'undefined' || $footer == null) {
        $footer = $("<button>").addClass("btn").attr({
            "type": "button",
            "data-dismiss": "modal"
        }).text("OK");
    }
    if (typeof dialogSize == "undefined") {
        dialogSize = "";
    } else {
        switch (dialogSize) {
            case "lg":
                dialogSize = "modal-lg";
                break;
            case "md":
                dialogSize = "";
                break;
            case "sm":
                dialogSize = "modal-sm";
                break;
            default:
        }
    }
    var $modal = popupFrame($header, $content, $footer);
    $modal.find(".modal-dialog").addClass(dialogSize);
    return $modal;
}

function popupFrame($header, $content, $footer) {
    return $("<div>").attr("id", randomString(10)).addClass("modal fade popup").append(
        $("<div>").addClass("modal-dialog").append(
            $("<div>").addClass("modal-content").append(
                typeof $header != 'undefined' ?
                    $("<div>").addClass("modal-header").append(
                        $header
                    ) : '',
                typeof $content != 'undefined' ?
                    $("<div>").addClass("modal-body").append(
                        $content
                    ) : '',
                typeof $footer != 'undefined' ?
                    $("<div>").addClass("modal-footer").append(
                        $footer
                    ) : ''
            )
        )
    );
}

/**
 * Generate random string
 * @param length
 * @param chars
 * @returns {string}
 */
function randomString(length, chars) {
    if (typeof chars == 'undefined') {
        chars = '0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ';
    }
    var result = '';
    for (var i = length; i > 0; --i) result += chars[Math.floor(Math.random() * chars.length)];
    return result;
}

