function capitalise(string) {
    return string.charAt(0).toUpperCase() + string.slice(1).toLowerCase();
}

function camelize(str) {
    return str.replace(/(?:^\w|[A-Z]|\b\w)/g, function (letter, index) {
        return index == 0 ? letter.toLowerCase() : letter.toUpperCase();
    }).replace(/\s+/g, '');
}

function describeServerRespondedError(errorCode) {
    switch (errorCode) {
        case 403:
            alertP("Permission denied", "You have no permission to perform this action.");
            break;
        case 404:
            alertP("Content not found", "The content could not be found.");
            break;
        case 500:
            alertP("Server error", "There was a server error which has been reported to our support team. Please try again later. Sorry for the inconvenience.");
            break;
    }
}

function localStorageAvailable() {
    var test = 'test';
    try {
        localStorage.setItem(test, test);
        localStorage.removeItem(test);
        return true;
    } catch (e) {
        return false;
    }
}

function setCookie(name, value, days) {
    if (days) {
        var date = new Date();
        date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
        //noinspection JSDuplicatedDeclaration
        var expires = "; expires=" + date.toGMTString();
    }
    else { //noinspection JSDuplicatedDeclaration
        var expires = "";
    }
    document.cookie = name + "=" + value + expires + "; path=/";
}

function getCookie(name) {
    var nameEQ = name + "=";
    var ca = document.cookie.split(';');
    for (var i = 0; i < ca.length; i++) {
        var c = ca[i];
        while (c.charAt(0) == ' ') c = c.substring(1, c.length);
        if (c.indexOf(nameEQ) == 0) return c.substring(nameEQ.length, c.length);
    }
    return null;
}

function removeCookie(name) {
    setCookie(name, "", -1);
}

function setLocalStorageOrCookie(key, value) {
    if (localStorageAvailable()) {
        localStorage.setItem(key, value);
    } else {
        setCookie(key, value, 365);
    }
}

function getLocalStorageOrCookie(key) {
    if (localStorageAvailable()) {
        return localStorage.getItem(key);
    } else {
        return getCookie(key);
    }
}

function removeLocalStorageOrCookie(key) {
    if (localStorageAvailable()) {
        localStorage.removeItem(key);
    } else {
        removeCookie(key);
    }
}

function getCookies() {
    var pairs = document.cookie.split(";");
    var cookies = {};
    for (var i = 0; i < pairs.length; i++) {
        var pair = pairs[i].split("=");
        cookies[pair[0]] = unescape(pair[1]);
    }
    return cookies;
}
